import React, { useState } from "react";
import axios from "axios";

export default function LoginPage() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleLogin = (e) => {
    e.preventDefault();
    // Add your login logic here using Axios POST request
    // For example:
    axios
      .post("/login", { email, password })
      .then((response) => {
        // Handle the response, e.g., redirect or show a success message
        console.log("Login successful!");
      })
      .catch((error) => {
        // Handle errors, e.g., display an error message
        console.error("Login failed:", error);
      });
  };

  const handleSignup = (e) => {
    e.preventDefault();
    // Add your signup logic here using Axios POST request
    // For example:
    axios
      .post("/signup", { email, password })
      .then((response) => {
        // Handle the response, e.g., redirect or show a success message
        console.log("Signup successful!");
      })
      .catch((error) => {
        // Handle errors, e.g., display an error message
        console.error("Signup failed:", error);
      });
  };

  return (
    <main className="login-page">
      <form className="login-form">
        <div className="login-form-content">
          <h3 className="login-form-title">Connexion Admin</h3>
          <div className="form-group">
            <label>Adresse mail</label>
            <input
              type="email"
              className="form-mail"
              placeholder="Adresse mail"
              required
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>
          <div className="form-group">
            <label>Mot de passe</label>
            <input
              type="password"
              className="form-control"
              placeholder="Mot de passe"
              required
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>
          <div>
            <button type="submit" className="login-btn" onClick={handleLogin}>
              Connexion
            </button>
            <button type="submit" className="signup-btn" onClick={handleSignup}>
              Inscription
            </button>
          </div>
        </div>
      </form>
    </main>
  );
}
